mapper.select:
select

from ${EUPS_SCHEMA}.EUPS_INV_TERM_INF

  
    AND  TLRID=#{tlrid,jdbcType=CHAR}
  
  
    AND  NODNO=#{nodno,jdbcType=CHAR}
  
  
    AND  INVTYP=#{invtyp,jdbcType=CHAR}
  
  
    AND  IVBEGNO=#{ivbegno,jdbcType=CHAR}
  
  
    AND  IVENDNO=#{ivendno,jdbcType=CHAR}
  
  
    AND  SEQNO=#{seqno,jdbcType=CHAR}
  
  
    AND  INVNUM=#{invnum,jdbcType=CHAR}
  
  
    AND  USEDAT=#{usedat,jdbcType=CHAR}
  
  
    AND  USENUM=#{usenum,jdbcType=CHAR}
  
  
    AND  CLRNUM=#{clrnum,jdbcType=CHAR}

select count(*) from ${EUPS_SCHEMA}.EUPS_INV_TERM_INF

  
    AND  TLRID=#{tlrid,jdbcType=CHAR}
  
  
    AND  NODNO=#{nodno,jdbcType=CHAR}
  
  
    AND  INVTYP=#{invtyp,jdbcType=CHAR}
  
  
    AND  IVBEGNO=#{ivbegno,jdbcType=CHAR}
  
  
    AND  IVENDNO=#{ivendno,jdbcType=CHAR}
  
  
    AND  SEQNO=#{seqno,jdbcType=CHAR}
  
  
    AND  INVNUM=#{invnum,jdbcType=CHAR}
  
  
    AND  USEDAT=#{usedat,jdbcType=CHAR}
  
  
    AND  USENUM=#{usenum,jdbcType=CHAR}
  
  
    AND  CLRNUM=#{clrnum,jdbcType=CHAR}

mapper.update:
update ${EUPS_SCHEMA}.EUPS_INV_TERM_INF
	set  INVNUM=#{invnum,jdbcType=CHAR},CLRNUM=#{clrnum,jdbcType=CHAR}
      where  TLRID=#{tlrid,jdbcType=CHAR} AND NODNO=#{nodno,jdbcType=CHAR}
