mapper.select:
select

from ${EUPS_SCHEMA}.EUPS_INV_TXN_INF

  
    AND  INVTYP=#{invtyp,jdbcType=CHAR}
  
  
    AND  IVBEGNO=#{ivbegno,jdbcType=CHAR}
  
  
    AND  IVENDNO=#{ivendno,jdbcType=CHAR}
  
  
    AND  USESEQ=#{useseq,jdbcType=CHAR}
  
  
    AND  STLNUM=#{stlnum,jdbcType=CHAR}
  
  
    AND  STLFLG=#{stlflg,jdbcType=CHAR}
  
  
    AND  ACTDAT=#{actdat,jdbcType=CHAR}
  
  
    AND  TLRID=#{tlrid,jdbcType=CHAR}
  
  
    AND  NODNO=#{nodno,jdbcType=CHAR}
  
  
    AND  QYNO=#{qyno,jdbcType=CHAR}
  
  
    AND  OLDSEQ=#{oldseq,jdbcType=CHAR}
  
  
    AND  OLDTRDATE=#{oldtrdate,jdbcType=CHAR}

select count(*) from ${EUPS_SCHEMA}.EUPS_INV_TXN_INF

  
    AND  INVTYP=#{invtyp,jdbcType=CHAR}
  
  
    AND  IVBEGNO=#{ivbegno,jdbcType=CHAR}
  
  
    AND  IVENDNO=#{ivendno,jdbcType=CHAR}
  
  
