mapper.update:
update ${EUPS_SCHEMA}.EUPS_LOT_AUT_PLN
	set STATUS=#{status,jdbcType=CHAR}
	where PLANNO=#{planno,jdbcType=CHAR} AND CRDNO=#{crdno,jdbcType=CHAR} AND STATUS='0'
