mapper.update:
update ${EUPS_SCHEMA}.EUPS_LOT_DRW_TBL
	set FLWCTL=#{flwctl,jdbcType=CHAR}
	where GAMEID=#{gameid,jdbcType=CHAR} AND DRAWID=#{drawid,jdbcType=CHAR} AND
		KENOID=#{kenoid,jdbcType=CHAR}

update ${EUPS_SCHEMA}.EUPS_LOT_DRW_TBL
	set FLWCTL=#{flwctl,jdbcType=CHAR},PAYFLG=#{payflg,jdbcType=CHAR},
		PAYMENT=#{payment,jdbcType=CHAR},XFEFLG=#{xfeflg,jdbcType=CHAR}
	where GAMEID=#{gameid,jdbcType=CHAR} AND DRAWID=#{drawid,jdbcType=CHAR} AND
		KENOID=#{kenoid,jdbcType=CHAR}

update ${EUPS_SCHEMA}.EUPS_LOT_DRW_TBL
	set PRZAMT=#{przamt,jdbcType=CHAR}
	where GAMEID=#{gameid,jdbcType=CHAR} AND DRAWID=#{drawid,jdbcType=CHAR} AND
		KENOID=#{kenoid,jdbcType=CHAR}
