mapper.select:
select

from ${EUPS_SCHEMA}.EUPS_LOT_PLN_CTL

  
    AND  GAMEID=#{gameid,jdbcType=CHAR}
  
  
    AND  DRAWID=#{drawid,jdbcType=CHAR}
  
  
    AND  DRAWNM=#{drawnm,jdbcType=CHAR}
  
  
    AND  BETDAT=#{betdat,jdbcType=CHAR}
  
  
    AND  BEGTIM=#{begtim,jdbcType=CHAR}
  
  
    AND  ENDTIM=#{endtim,jdbcType=CHAR}
  
  
    AND  TXNSTS=#{txnsts,jdbcType=CHAR}

select count(*) from ${EUPS_SCHEMA}.EUPS_LOT_PLN_CTL

  
    AND  GAMEID=#{gameid,jdbcType=CHAR}
  
  
    AND  DRAWID=#{drawid,jdbcType=CHAR}
  
  
    AND  DRAWNM=#{drawnm,jdbcType=CHAR}
  
  
    AND  BETDAT=#{betdat,jdbcType=CHAR}
  
  
    AND  BEGTIM=#{begtim,jdbcType=CHAR}
  
  
    AND  ENDTIM=#{endtim,jdbcType=CHAR}
  
  
    AND  TXNSTS=#{txnsts,jdbcType=CHAR}

mapper.update:
updateTxnsts ${EUPS_SCHEMA}.EUPS_LOT_PLN_CTL
	set ENDTIM=#{endtim,jdbcType=CHAR},TXNSTS=#{txnsts,jdbcType=CHAR}
	where GAMEID=#{gameid,jdbcType=CHAR} AND DRAWID=#{drawid,jdbcType=CHAR}
