select

	from ${EUPS_SCHEMA}.EUPS_TRANS_JOURNAL
		where THD_CUS_NO=#{payNo,jdbcType=CHAR}
		AND EUPS_BUS_TYP=#{eupsBusTyp,jdbcType=CHAR}
		AND (
				TXN_DTE between #{startDate,jdbcType=CHAR} AND #{endDate,jdbcType=CHAR}
		)

select

	from ${EUPS_SCHEMA}.EUPS_TRANS_JOURNAL
		where EUPS_BUS_TYP=#{eupsBusTyp,jdbcType=CHAR}
		AND   COM_NO=#{comNo,jdbcType=CHAR}
		AND   TXN_STS !='S'
