select

from ${GDEUPSB_SCHEMA}.GDEUPS_BATCH_CONSOLE_INFO

	
		AND BAT_NO=#{batNo,jdbcType=CHAR}
	
	
		AND FLE_NME=#{fleNme,jdbcType=CHAR}
	
	
		and EUPS_BUS_TYP = #{eupsBusTyp,jdbcType=VARCHAR}

SELECT * FROM (SELECT * FROM EUPS.EUPS_BATCH_INFO_DETAIL C WHERE C.BAT_NO IN ( select DISTINCT BAT_NO from eups.EUPS_BATCH_CONSOLE_INFO WHERE COM_NO in (select DISTINCT com_no from GDEUPSB.GDEUPS_BATCH_CONSOLE_INFO where EUPS_BUS_TYP=#{eupsBusTyp, jdbcType=CHAR} and EXE_DTE=#{txnDte, jdbcType=DATE}
AND COM_NO=#{comNo,jdbcType=CHAR}
) AND EXE_DTE=#{txnDte, jdbcType=DATE} AND BAT_STS='S' )) A, (SELECT COALESCE(SUM(TOT_CNT), 0) TOTCNT, COALESCE(SUM(TOT_AMT), 0) TOTAMT, COALESCE(SUM(SUC_TOT_CNT), 0) SUCTOTCNT, COALESCE(SUM(SUC_TOT_AMT), 0) SUCTOTAMT, COALESCE(SUM(FAL_TOT_CNT), 0) FALTOTCNT, COALESCE(SUM(FAL_TOT_AMT), 0) FALTOTAMT FROM EUPS.EUPS_BATCH_CONSOLE_INFO WHERE BAT_NO IN ( select DISTINCT BAT_NO from eups.EUPS_BATCH_CONSOLE_INFO WHERE COM_NO in (select DISTINCT com_no from GDEUPSB.GDEUPS_BATCH_CONSOLE_INFO where EUPS_BUS_TYP=#{eupsBusTyp, jdbcType=CHAR} and EXE_DTE=#{txnDte, jdbcType=DATE}
AND COM_NO=#{comNo,jdbcType=CHAR}
) AND EXE_DTE=#{txnDte, jdbcType=DATE} AND BAT_STS='S' )) B

