select

from ${GDEUPSB_SCHEMA}.GDEUPS_ELE_TMP
	where 
   PAYMENT_RESULT!='00'
    AND  TXN_DTE=#{txnDte,jdbcType=DATE}
    AND  SUBSTR(COM_NO,1,4)=#{comNo,jdbcType=CHAR}

