mapper.select:
select a.TLogNo, a.DrawId, a.KenoId, a.PlayId,a.BetMul, a.BetLin, a.BetMod, b.TotPrz, a.TxnTim, a.TxnLog
from GDEUPSB.Lot_Prz_Ctl b, GDEUPSB.Lot_Txn_Jnl a

	
	 
	 	a.GameId=b.GameId and a.DrawId=b.DrawId and a.KenoId=b.KenoId and a.TxnLog=b.TxnLog and a.TLogNo=b.TLogNo and CrdNo=#{crdNo} and BetDat between #{begDat} and #{endDat} and BetTyp=#{betTyp} and TxnSts='S' and a.GameId=#{gameId}
	 
	 
	 	a.GameId=b.GameId and a.DrawId=b.DrawId and a.KenoId=b.KenoId and a.TxnLog=b.TxnLog and a.TLogNo=b.TLogNo and CrdNo=#{crdNo} and BetDat between #{begDat} and #{endDat}  and TxnSts='S' and a.GameId=#{gameId}
	 
	

order by TxnTim desc

