where GAME_ID = #{gameId,jdbcType=CHAR} AND DRAW_ID = #{drawId,jdbcType=CHAR}

update gdeupsb.GDEUPS_LOT_TXN_JNL

    CHK_FLG = '2', CHK_TIM = #{chkTim,jdbcType=CHAR}

where GAME_ID = #{gameId,jdbcType=CHAR} AND DRAW_ID = #{drawId,jdbcType=CHAR} 
AND CHK_FLG = '0' AND TXN_STS in ('S', 'T')

