select a.T_Log_No, a.Draw_Id, a.Keno_Id, a.Play_Id,a.Bet_Mul, a.Bet_Lin, a.Bet_Mod, b.Tot_Prz, a.Txn_Tim, a.Txn_Log
from GDEUPSB.GDEUPS_Lot_Prz_Ctl b, GDEUPSB.GDEUPS_Lot_Txn_Jnl a

	
	 
	 	a.Game_Id=b.Game_Id and a.Draw_Id=b.Draw_Id and a.Keno_Id=b.Keno_Id and a.Txn_Log=b.Txn_Log and a.T_Log_No=b.TLogNo and Crd_No=#{crdNo} and Bet_Dat between #{begDat} and #{endDat} and Bet_Typ=#{betTyp} and Txn_Sts='S' and a.Game_Id=#{gameId}
	 
	 
	 	a.Game_Id=b.Game_Id and a.Draw_Id=b.Draw_Id and a.Keno_Id=b.Keno_Id and a.Txn_Log=b.Txn_Log and a.T_Log_No=b.TLogNo and Crd_No=#{crdNo} and Bet_Dat between #{begDat} and #{endDat}  and Txn_Sts='S' and a.Game_Id=#{gameId}
	 
	

order by Txn_Tim desc
